import React from 'react'
import { useGlobalContext } from './Context'

const Search = () => {
  const {query, searchPost} = useGlobalContext()
  return (
    <>
    <h1>Tech News</h1>
    <form >
      <div>
        <input type="text" placeholder='Search Here' 
        value={query}
        onChange={e=>e.target.value}
        />
      </div>
    </form>
    </>
  )
}

export default Search
